css:
@-moz-document domain("stash.agiledigital.com.au")
{
  .removed {
    background-color: /*[[deleted_line]]*/ !important;
  }

  .removed .bitbucket-gutter-marker {
    background-color: /*[[deleted_line]]*/ !important;
  }

  .removed .comment-box {
    background-color: /*[[deleted_line]]*/ !important;
  }

  .removed .ediff-change,
  .removed .ediff-delete {
    background-color: /*[[deleted_word]]*/ !important;
  }

  .added {
    background-color: /*[[added_line]]*/ !important;
  }

  .added .bitbucket-gutter-marker {
    background-color: /*[[added_line]]*/ !important;
  }

  .added .comment-box {
    background-color: /*[[added_line]]*/ !important;
  }

  .added .ediff-add,
  .added .ediff-change {
    background-color: /*[[added_word]]*/ !important;
  }
}
