css:
@-moz-document domain(gitlab.com)
{
body, .card,.info-well {
	background-color:black;
	color:#0f0;
	filter:invert();
}
body.ui-indigo .navbar-gitlab {
	filter:invert();
	border-color:green;
}
p {
	color:#0f0;
}
h1, h2, h3, h4, h5, h6 {
	color:hotpink;
}
.code.white .n,a {
	color:orange;
}
.dropdown-menu, .dropdown-menu-nav {
	filter:invert();
}
img {
	filter:invert();
}
}
