/* ==UserStyle==
@name         QuickScroll
@description  QuickScroll - scroll for me. СсылОчки внизу ведут на Real Dark Stylus
@namespace    https://github.com/DopeCatSoft/RealDarkStylus
@homepageURL  https://github.com/DopeCatSoft/RealDarkStylus
@updateURL    https://dl.dropboxusercontent.com/s/jma4tcddxeq1ovv/realdarkstylus.user.css?dl=0
@supportURL   https://github.com/DopeCatSoft/RealDarkStylus
@version      1.0.0
@author       dopecatsoft (http://dopecatsoft.c1.biz)
@license      CC-BY-NC-4.0
@preprocessor stylus
==/UserStyle== */
@media(prefers-color-scheme:dark) {
	:root {
		--scroll: #282828;
		--thumb: rgba(255,255,255,0.51);
	}
}
@media(prefers-color-scheme:light) {
	:root {
		--scroll: rgba(255,255,255,0.51);
		--thumb: #191919;
	}
}
::-webkit-scrollbar-track {
	background: var(--scroll)
}
::-webkit-scrollbar {
	width: .62vw;
	height: 1vh;
	background: var(--scroll)
}
::-webkit-scrollbar-thumb {
	background: var(--thumb)
}
