css:
@-moz-document domain("nerdcave.com") {
  .bg-white {
      background-color: #282828;
  }


  .bg-blue-grey-50,
  .bg-grey-100,
  .bg-grey-200,
  .bg-grey-300,
  .bg-grey-800 {
      background-color: #1c1c1c;
  }

  .bg-blue-50 {
      background-color: #000;
  }

  .text-grey-800 {
      color:#dedede ;
  }
  .text-blue-700 {
      color: #b9b7b7;
  }
  .border-amber-400-accent,
  .bg-amber-400-accent {
      background-color: transparent;
      color: #595959;
      border-color: transparent;
  }

  * {
      border-color: #333 !important;
  }
}
