css:
@-moz-document domain("ngusav.es") {
body {
    background-color: #222228;
    color: #fefeff;
}

a {
    color: #be40ff;
}

.main .top-row {
    background-color: #111;
    border-bottom: 1px solid #222;
}

.table {
    color: #ddd;
}

.table td, .table th {
    border-top: 1px solid #6c757d;
}

.challenges .pip.incomplete {
    background-color: #333338;
}

.drag-drop-zone {
    border: 3px dashed #82f;
    background-color: #333;
}

.drag-drop-zone:hover {
    background-color: #111;
}
}
