@-moz-document domain("87percenttranslation.wordpress.com") {
html, body {
    font-family: "/*[[etfont]]*/", sans-serif !important;
    background-color: #111111 !important;
    scrollbar-color: #444444 #222222;
}
div#page.site {
    background-color: #222222 !important;
}
p {
    font-size: /*[[etsize]]*/ !important;
    line-height: 1.4em;
    color: #d4d4d4;
}
div#primary.content-area main#main {
    margin: 0 !important;
}
div#secondary.widget-area {
    display: none !important;
}
}
