css:
@-moz-document domain("zoom.us") {
/* Hide all ui elements except video and webcam */

/* Hide footer */
.footer {
    display: none !important;
}

/* Hide indicator "You are viewing <name> screen ... View Options" */
.sharee-container__indicator {
    display: none;
}

/* Hide indicator "Recording" */
.rec-section {
    display: none;
}

/* Hide indicator "Talk:<name>" */
.asntip {
    display: none;
}

}
