css:
@-moz-document domain("fretastic.com") {
.basic-fretboard-container .row .basic-fret {
    position: relative;
}
.basic-fretboard-container .row:not(:first-child):not(:last-child) .basic-fret:nth-child(2n+3):not(:nth-child(12n+11)):not(:nth-child(13)):before, .basic-fretboard-container .row:not(:first-child):not(:last-child) .basic-fret:nth-child(12n+12):before {
    position: absolute;
    content: '';
    background:rgba(96,96,96,.3);
    top:0;
    bottom:0;
    left:calc(4px + 10%);
    right: 10%;
}
}
