css:
@-moz-document url-prefix("https://animetake") {
    h1, h2, h3, h4, h5, p, a, li, div, b, .latestep_subtitle, .latestep_parent {
        color: #f5f5f5 !important;
    }

    .latestep_title a, .latest-serie-sidebar a {
        color: #7289DA !important;
    }

    /* i really want this to be global */
    body, .container, .list-group-item, .well {
        background-color: #22222D;
    }

    .breadcrumb, .alert, .list-group-item:hover, .latestep_wrapper {
        background-color: #2B2B36 !important;
    }

    .animeinfo-div, .well:hover, .animelist_poster .thumbnail {
        background: #2B2B36 !important;
    }

    .animelist_alfabet_list, .scroll-top-wrapper, .btn, .btn b {
        color: black !important;
    }
}
