css:
@-moz-document domain("nl.movim.eu") {
body nav ~ main {
    width: calc(100% - 7rem);
}

body > nav ~ main, body > nav:hover ~ main {
    width: calc(100% - 7rem);
}

body > nav {
    width: 7rem;
}

body > nav:hover {
    width: 7rem;
}

main > div:first-child:nth-last-child(2) + div {
    width: 350px;
}

main > div:first-child:nth-last-child(2) {
    width: calc(100% - 350px);
}
}
