css:
@-moz-document domain("mewe.com") {
.c-mw-post .post_text .text-truncate_wrapper h1, 
.c-mw-post .post_text .text-truncate_wrapper h2, 
.c-mw-post .post_text .text-truncate_wrapper h3, 
.c-mw-post .post_text .text-truncate_wrapper h4, 
.c-mw-post .post_text .text-truncate_wrapper h5, 
.c-mw-post .post_text .text-truncate_wrapper h6 {    
    display: block !important;
    margin-top: 1.5rem !important;
    margin-bottom: .5rem !important;    
    font-weight: 500 !important;
    line-height: 1.25 !important;    
}


.c-mw-post .post_text .text-truncate_wrapper h1:first-child, 
.c-mw-post .post_text .text-truncate_wrapper h2:first-child, 
.c-mw-post .post_text .text-truncate_wrapper h3:first-child, 
.c-mw-post .post_text .text-truncate_wrapper h4:first-child, 
.c-mw-post .post_text .text-truncate_wrapper h5:first-child, 
.c-mw-post .post_text .text-truncate_wrapper h6:first-child { 
    margin-top: 0 !important;
}


.c-mw-post .post_text .text-truncate_wrapper h1 {    
    font-size: 2.25rem;
    
}

.c-mw-post .post_text .text-truncate_wrapper h2 {
    font-size: 2rem;
    
}

.c-mw-post .post_text .text-truncate_wrapper h3 {
    font-size: 1.75rem;

}

.c-mw-post .post_text .text-truncate_wrapper h4 {
    font-size: 1.5rem;

}

.c-mw-post .post_text .text-truncate_wrapper h5 {
    font-size: 1.25rem;

}

.c-mw-post .post_text .text-truncate_wrapper h6 {
    font-size: 1rem;
}


blockquote {
    
    margin-block-start: 1em;
    margin-block-end: 1em;
    margin-inline-start: 40px;
    margin-inline-end: 40px;
    margin: 1rem 0 1rem 1rem;
    font-size: 1.25rem;
    display: block;
    line-height: 1.5em;
    border-left: 4px solid;
}

.c-text-truncate, .text-truncate_wrapper {
    max-height: unset !important;    
}

.text-truncate_button.btn-show.btn-reset.h-trim.group-color {    
    display: none !important;
}
}
