css:
@-moz-document domain("tapology.com") {

.fighterFightResults .result:not(:hover):not([data-result=cancelled]) {
  border-color: #ccc !important;
}
.fighterFightResults .result:not(:hover):not([data-result=cancelled]):before {
  display: none !important;
}

.fighterFightResults .result:not(:hover):not([data-result=cancelled]) .lead {
  display: none !important;
}

.fighterFightResults .result:not(:hover) .notes {
  margin-top: 0 !important;
}

.fighterFightResults .result:not(:hover) .record {
  display: none !important;
}

}
