css:
@-moz-document domain(recalldev.com) {
.BannerRedesign {
    background: url(https://i.imgur.com/vv8wxVM.png) repeat-x
}
#NavigationRedesignBannerContainer a.btn-logo, .NavigationRedesign a.btn-logo:visited, #NavigationRedesignBannerContainer a.btn-logo:hover {
    background: url(https://i.imgur.com/C3nVuVS.png) no-repeat top;
}
#Footer.LanguageRedesign {
    background: #353535;
}
#Footer.LanguageRedesign .Legalese {
    color: #6B6B6B;
}
#Footer.LanguageRedesign .legal {
    border-top: 1px solid #6B6B6B;
    padding-top: 20px;
}
html {
    background-color: #353535;
}
#NavigationRedesignBannerContainer .HeaderDivider {
    Border-left: 1px solid #6B6B6B;
}
}
