css:
@-moz-document regexp("https://boxnovel.com/novel/[^/]+?/.+") {
.site-header, .adbox, div + br {
    display: none !important;
}
.wrap .body-wrap, body.text-ui-light .site-header .c-header__top {
    background-color: #1a1a1a !important;
}
h1, h2, h3, h4, h5, h6, a, a:visited {
	color: #ebebeb !important;
}
p, div {
    color: #8c8c8c !important;
}
.wp-manga-action-button {
    color: #ebebeb !important;
    background-color: #eb3349 !important;
}
}
