css:
@-moz-document url-prefix("https://isekailunatic.wordpress.com/"), url-prefix("https://isekailunatic.com/") {
/* Background */
body, ul {
    background-color: #1a1a1a !important;
}
/* Text and Story */
p, strong {
    color: #8c8c8c !important;
    margin-bottom: 0.5em;
}
/* Titles and Links */
a, h1, h2, h3, h4, h5, h6 {
    color: #ebebeb !important;
}
/* Reading Area */
.entry-content {
	font-size: 22px;
}
.site-main {
    margin-left: auto;
	margin-right: auto;
    float: none;
}
@media (min-width: 768px) {
.site-main {
	width: 750px;
}
}
@media (min-width: 992px) {
.site-main {
    width: 970px;
}
}
@media (min-width: 1200px) {
.site-main {
    width: 1170px;
}
}
}
