css:
@-moz-document url-prefix("https://ficbook.net/tags"), url-prefix("https://ficbook.net/tag-categories/") {
  .list-tags > li > .small {
      position: absolute;
      display: none;
  }

  /*[[hover-selector]]*/ {
      z-index: 1;
      display: block;
      width: 33%;
  }

  /*[[hover-selector]]*/ > div {
      background-color: #f6ecdaff;
      border: 1px solid #542a00;
  }
  
  /*[[if-arrow]]*/
}
