css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("www.npostart.nl") {
npo-click-away-bar {
    display: none;
}
.npo-header-episode-meta {
    width: 100% !important;
}

.xnpo-header-episode-content {
    margin: 10px auto;
    max-width: 1700px !important;
}

div.container {
    max-width: 100%;
}

/* Strip top left menu */
nav .npo-menu-user li .nav-item, nav .npo-pages li .nav-item {
    margin: 0 .3em;
}

#npo-menu-register,
.npo-menu-user-visitor {
    display: none !important;
}

nav .npo-menu-user .npo-menu-search #npo-search-toggle {
    width: unset;
}

nav .npo-menu-user .npo-menu-search #npo-search-toggle::after {
    content: "";
}

.npo-spotlight {
    height: 250px;
}

.npo-spotlight .button-box {
	display: none;
}

.npo-spotlight .npo-spotlight-header-meta {
    padding: 0;
}

.npo-lane .npo-lane-title,
.npo-five-grid-title {
    margin: 0;
}

/* NPO Plus banner */
.ticket-banner {
    display: none;
}


/* Programme overview */
.npo-header-franchise .npo-header-franchise-buttons {
    display: none;
}

.icon-facebook {
    display: none;
}

.npo-footer {
    display: none !important;
}

#live-header > div:nth-child(1) {
    margin: 0 !important;
    max-width: 100% !important;
}

div.container:nth-child(2) {
    margin: 0;
}
}
