css:
@-moz-document domain("stackoverflow.com"), domain("superuser.com"), domain("askubuntu.com"), domain("serverfault.com"), domain("stackexchange.com") {
/*Dark Scrollbar*/
::-webkit-scrollbar { width: 10px; }
::-webkit-scrollbar-track { background: #fff0; }
::-webkit-scrollbar-thumb { background: #222; }
::-webkit-scrollbar-thumb:hover { background: #252525; }

#sidebar, .sidebar,
.left-sidebar,
.top-bar,
.ml12,
#post-form, .bottom-notice,
.site-footer, .site-header,
.py24
{
  display: none;
}

body
{
  padding-top: 0;
}

#content
{
  border: none;
  margin: auto;
  width: 60%;
}

#mainbar, .mainbar
{
  width: 100% !important;
}

/*----------------DARK THEME BELOW---------------*/

.s-btn-group,
.comments
{
  background: #323537;
}

body, html,

#content
{
    background: #2d3032;
}

pre, code,
.owner,
.post-tag, .geo-tag, .container .chosen-choices .search-choice, .container .chosen-container-multi .chosen-choices li.search-choice,
.s-btn__muted.is-selected, .s-btn__muted:hover, .s-btn__muted:focus, .s-btn__muted:active,
.s-notice__info, .s-banner__info,

.bg-white, .h\:bg-white:hover, .f\:bg-white:focus,

.hero-box
{
    background: #242729 !important;
}

.p-bg-purple-light, .bg-powder-100, .h\:bg-powder-100:hover, .f\:bg-powder-100:focus
{
    background-color: #2d3032 !important;
}

body, code,
#question-header .question-hyperlink,

.pun, .pln,

.post-tag, .geo-tag, .container .chosen-choices .search-choice, .container .chosen-container-multi .chosen-choices li.search-choice,
.s-notice
{
  color: #ccc !important;
}

*,
.bc-black-2
{
  border: none !important;
  box-shadow: none !important;
}

.post-text a:not(.post-tag):not(.badge-tag):not(.s-link__visited):visited, .comment-copy a:visited, .wmd-preview a:not(.post-tag):not(.badge-tag):not(.s-link__visited):visited
{
  color: #be1e2d;
}

header.siteHeader h1
{
    filter: invert(1) hue-rotate(180deg) saturate(2) brightness(.8);
}

.product-hero-background
{
    filter: invert(1) hue-rotate(180deg) saturate(3) brightness(2);
  background-color: #fff;
    mix-blend-mode: color-dodge;
}

.product-hero-background * 
{
  color: #333 !important;
}
#content > div.py128.product-hero-background.d\:fc-white > div > a.grid--cell.bc-orange-2.ba.fc-orange-400.bg-white.d\:bg-black-900.py12.px24.bar-sm.js-scroll-link.js-gps-track{
  color: #fff !important;
}
}
