css:
@-moz-document domain("www.lemonde.fr") {
section[class="article__wrapper   article__wrapper--premium"] {
    width: 180% !important;
}

p[class="article__cite"] {
    width: 160% !important;
    text-align: justify;
    text-indent: 30px;
    max-width: 160% !important;
}

section[class="article__content"],
p[class="article__paragraph article__paragraph--lf"],
figure[class="article__media article__media--default "],
picture,
figcaption {
    width: 130% !important;
    text-align: justify;
    text-indent: 30px;
}

p[class="article__paragraph "],
ul[class="article__unordered-list"],
h3[class="article__question"],
div[class="multimedia-embed snippet"],
figure,
section[class="catcher catcher--inline"],
section[class="catcher catcher--reference js-catcher"] {
    width: 160% !important;
    text-align: justify;
    text-indent: 30px;
}

h1[class="article__title"],
p[class="article__desc"] {
    width: 121% !important;
}

section[class="article__content article__content--comments old__article-content-single"] {
    width: 160% !important;
}
}
