css:
@-moz-document domain("pyontranslations.com") {
/* Background */
body, ul, .navbar, .post-inner-content, .well, .comment-body, .post-navigation a {
    background-color: #1a1a1a !important;
}
.tagcloud a, .btn-sm {
	background-color: #8c8c8c !important;
}
/* Text and Story */
p, li, .easy-footnote-single {
    color: #8c8c8c !important;
    margin: 0 0 0.6rem !important;
    font-size: 18px !important;
}
/* Titles and Links */
strong, h1, h2, h3, h4, h5, h6, .dropdown-menu > li > a {
    color: #ebebeb !important;
}
/* Reading Area */
.main-content-inner {
    width: auto !important;
}
}
