css:
@-moz-document domain("challonge.com") {
body.-venatus .container-fluid.-with-content-gutters {
    padding: 0;
}

body.-venatus .container-fluid.-with-content-gutters {
    padding-left: 0;
    padding-right: 0;
    max-width: 100%;
}

.container-fluid.-centered, .container-fluid.-width-limited {
    max-width: 100%;
}

.cake-unit,
.vm-footer,
.vm-skin {
    display: none;
}

/* extra anti-ad */
#pnetContVid, #pbnetVideo, #sbbfrcc, [id^="trackContent_"], [id^="criteo"], [id^="vm3"] {
    display: none !important;
}

body {
    padding-top: 64px !important;
}

.raiSkinDesktop .title-stripe, .raiSkinDesktop .content-wrapper, .raiSkinDesktop .footer-public, .raiSkinDesktop .session-layout, .raiSkinDesktop .tournament-banner, .raiSkinDesktop .event-banner, .raiSkinDesktop .profile-banner, .raiSkinDesktop .event-section, .raiSkinDesktop .bare-footer, .raiSkinDesktop > .container {
    max-width: none !important;
}
}
