css:
@-moz-document domain('skyscrapercity.com') {
    .structItem-title {
        width: calc(100% - 250px);
        display: inline-block;
    }
    .structItem-minor {
        text-align: right;
        width: 250px;
        display: inline-block;
    }
    .structItem-cell--main {
        min-height: 45px;
        display: flex;
        align-items: center;
    }
    ul.structItem-parts {
        display: none;
    }
    .truncate-username-text {
        display: none;
    }
}
