css:
@-moz-document domain("stackexchange.com"), domain("stackoverflow.com"), domain("serverfault.com") {
#content {
  width: 100%;
  border: none;
  max-width: 1400px;
}

#mainbar, .mainbar {
  width: 67%;
}

.community-option,
#left-sidebar,
.bottom-notice {
    display: none;
}

.top-bar._fixed { 
    position: absolute; 
}
}
