css:
@-moz-document url-prefix(https://cs125.cs.illinois.edu/learn/2020) {
Multiple
body {
   	background: #252525; 
   	color: #fff;
}

.hljs {
	background: #383838;   
    color: #fff;
}

.hljs-keyword, .cm-s-default .cm-keyword {
    color: #ff3f4f;
}

.hljs-title, .cm-s-default .cm-def {
    color: #c98fd3;
}

.bespoke-slide pre.highlight code {
 	color: #fff;   
}

.bespoke-slide code {
 	color: #f33;   
}

td.hljs-ln-numbers {
    background: #2f2f2f;
 	color: #fee;
}

.CodeMirror {
 	background: #000;
    color: #ddf;
}

.CodeMirror-gutters {
 	border-right: #000;   
    background-color: #121212;
}

.CodeMirror-linenumbers {
 	border-right: #000;   
    background-color: #121212;   
}

.CodeMirror-cursor {
    border-left: 1px solid white;
}

polygon {
    stroke: #fe5656;
}
}
