@-moz-document domain("www.coinbase.com") {
/* Coinbase Pitchblack by SLTW Version 0.6*/

html {filter: invert(100)}


.colorsusedinthistheme {
  color: #0000;
  color: #000000;
  color: #9f9f9f;
  color: #8a8a8a;
  color: #595858;
  color: #00ff00;
  color: #02aa6f;
  color: #aa8602;
  color: #ff00ff;
  color: #ffffff;
  color: #ffffffcf;
  color: #f2f2f2;
  }

.idMTQs {  background-image: linear-gradient(#ffffff, #f2f2f2);}

.bfhPcC {  background-image: linear-gradient(#f2f2f2, #ffffff);}

.dojklx {  background-color: #ffffffcf;}


.emtOes {  background-image: linear-gradient(#ffffff,#f2f2f2);}

.fEuUSx {  background-image: linear-gradient(#f2f2f2, #ffffff);}


  /*coinbase logo*/
  .bPTUo path {filter: invert(100); fill: #008b00;}


/*invert the profile picture so it looks normal*/
[class="Avatar__AvatarImage-hgevj6-0 dyeWdJ Dropdown__StyledAvatar-sc-9c4nka-2 fwUXTW"],[class="Avatar__AvatarImage-hgevj6-0 fzdyiV ChangePicture__UserPicture-sc-1j7rzba-1 fSNWvM"] {filter: invert(100);}

/*button design*/
.gzOkyf {filter: invert(100); background-color: #0000; border-color: #02aa6f;}
.gzOkyf:hover {filter: invert(100); background-color: #000; animation: gzOkyf .4s linear;}
@keyframes gzOkyf {
  from {box-shadow: 0px 0px 10px 4px #02aa6f;}
  to {box-shadow: none;}
}


.hyCMpn {filter: invert(100); background-color: #0000; border-color: #aa8602;}
.hyCMpn:hover {filter: invert(100); background-color: #0000; border-color: #aa8602; animation: hyCMpn .4s linear;}
@keyframes hyCMpn {
  from {box-shadow: 0px 0px 10px 4px #aa8602;}
  to {box-shadow: none;}
}


.lcXJye {filter: invert(100); background-color: #0000; color: #ffffff; border-color: #595858;}
.faFfbW:hover, .lcXJye:hover, .lglzYH > button:hover {filter: invert(100); background-color: #0000; border-color: #aa8602; color: #aa8602;}

  
  
/*Watchlist button*/
  .kdIrPY { border: 2px solid #f0f; box-shadow: #f0f 0px 0px 5px 2px}
  

/*Color of favorites selected (not selected = path {stroke: #8a8a8a;}) */
.fQAJDk,.fQAJDk path {filter: invert(100); fill: #00ff00; stroke: #00ff00;}

/*Market is up*/
.eFmaBt  {filter: invert(100); color: #00ff00;}


/*Font adjustments (inside graph)*/
.geTkzT {filter: invert(100); color: #595858;}
.geTkzT:hover {filter: invert(100); color: #ffffff;}
.faYwxd {filter: invert(100); color: #02aa6f;}

/*Font adjustment - Current balance*/
.bCFSYI {filter: invert(100); color: #02aa6f;}



/*alerts like cookie notifier and such*/
.alert {filter: invert(100);
    padding: 8px 35px 8px 14px;
    margin-bottom: 20px;
    text-shadow: 0 1px 0 rgba(255,255,255,.5);
    background-color: #fcf8e3;
    border: 1px solid #fbeed5;
    border-radius: 4px;
 
}
.alert > p {color: #000 !important;}


  
/*overlay background test*/
  .hFwduY__overlay, .klfbjT {background-color: rgba(255, 255, 255, .83)}
  .hFwduY__content, .fRPUxe {box-shadow: 0 0 15px 5px #dd3ef5}


/*graph design*/
.bvriKK, .camJhM 
 {
  box-shadow: 0px 0px 10px 2px #9f9f9f;
  border-radius: 5px;
}


[type="priceValues"]  {;
    stroke-width: 1.7;
    fill: rgba(0, 0, 0, .06);
    pointer-events: none;
    stroke: #ff00ff;
}

  /*Marktentwicklung*/
  
  .gOekIq {filter: invert(100); color: #0f0; animation: market 1s alternate infinite;}
  .gOekIq:after {content:" â–²"}
  
  @keyframes market {
    from {text-shadow: 0px 0px 12px #0f0}
    to {text-shadow: 0px 0px 5px #003100}
  }
  
  
  /*Entwicklung*/
  /*negativ*/
  .jhBYcZ {filter: invert(100); color: #d53200}
  /*positiv*/
  .QQmsQ, .kRXcml {filter: invert(100); color: #0f0}
  
  
  
  /*handeln und kaufen/verkaufen button*/
  .bBOebR {filter: invert(100); background-color: #000; border: solid 2px #008f00; color: #dbd9d9}
  .bBOebR:hover { background-color: #000; border-color: #0f0}
  
  
  
  /*andere buttons*/

  .isVEuC, .cUfLtg {filter: invert(100); background: #000; border-color: #008f00}
  .isVEuC:hover { background-color: #000; border-color: #0f0}  
  
  .fzSyAp, .jBanqt {filter: invert(100); background: #000; border-color: #00328f}
  .fzSyAp:hover, .jBanqt:hover {background: #000; border-color: #0059ff}



/* ausrufezeichen icon*/
.cVPOMy {filter: hue-rotate(136deg)}


/*icon and image invert corrected*/
.iFDhPJ, img {filter: invert(100)}

  
/*notification banner*/
.gHHEIg {filter: invert(100); background:black; border: 1px solid #005eff;}
  

/*Kontolimits*/

.khJndk, .eNBHqQ {filter: invert(100); border: 1px solid #0f0; background-color: #000; }

.jHCLuo {fill: #0f0;}
.hmEbhy { filter: invert(100); fill: #0f0}

/*active site*/

.gLCNdf.active {filter: invert(100); color: #0f0; }
.gLCNdf.active path {fill: #0f0; }
.gLCNdf:hover { background: none;}
.ddCKIy {background: transparent; }




/*testing*/
.jKQmDb {filter: invert(100); background: #181818}
.styles__AllocationBarInner-sc-1yfdrpu-7 {filter: invert(100)}
.styles__IconWrapper-vb6gfj-7 {filter: invert(0)}



}
