css:
@-moz-document url-prefix("https://www.roblox.com/trades") {
div.text-lead {
    margin-top: 0px !important;
}
.trades-list-detail .col-xs-12 {
    transform: scaleY(-1);
}
.trade-list-detail-offer {
    transform: scaleY(-1);
}
.trades-list-detail .trade-list-detail-offer:first-child .rbx-divider {
    display: block !important;
}
.trades-list-detail .trade-list-detail-offer:last-child .rbx-divider {
    display: none !important;
}
.trade-row .thumbnail-2d-container, .avatar-headshot-sm {
    width: 35px;
    height: 35px;
}
.trade-row .trade-row-details>div {
    height: 35px;
}
}
