css:
@-moz-document domain("newegg.com") {
#GalleryContainer2011 {
    top: 10% !important;
    left: 10% !important;
    width: 80%;
    height: 80%;
}

#GalleryContainer2011 .maincontent {
        height: calc(100% - 26px);
}

#GalleryContainer2011 #widViewer {
    height: 100%;
    width: calc(100% - 202px);
}

.product-panel {
    height: calc(100% - 35px);
    width: calc(100% - 35px);
}

.image-panel {
    height: calc(100% - 35px);
    width: 100%;
}

.image-panel img {
    /* width: 100% !important;
    height: 100% !important; */
    object-fit: contain;
}

.tool-panel {
    text-align: center;
}

.tool-panel div.zoom {
    float: initial;
    margin: auto !important;
}

}
