css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("gazeta.pl"), domain("cafe.pl"), domain("deser.pl"), domain("domosfera.pl"), domain("edziecko.pl"), domain("emetro.pl"), domain("gamecorner.pl"), domain("gazetadom.pl"), domain("gazetapraca.pl"), domain("infomuzyka.pl"), domain("kotek.pl"), domain("logo24.pl"), domain("plotek.pl"), domain("popcorner.pl"), domain("sport.pl"), domain("technonews.pl"), domain("wyborcza.biz"), domain("wyborcza.pl"), domain("wysokieobcasy.pl"), domain("zczuba.pl") {

  *[id^="Ad"],
  *[id^="autopromo"],
  *[id^="ban"],
  *[id^="mod_"],
  *[class^="ad"],
  *[class^="autopromo"],
  *[class^="eCommerce"],
  *[class*="tradedoublerHolder"] {
    display:none !important;
  }
}


@-moz-document domain("gazeta.pl") {

  #autopromo,
  #happyday,
  #main_alert,
  #mod_ads,
  #mod_offer,
  #mod_ads_bottom,
  #tradedoublerHolder,
  div.mod_ad,
  div[id^="ebPanelDiv"]{
    display:none !important;
  }
  #mod_offer_small {
    visibility:hidden !important;
    height:10px !important;
  }
}

@-moz-document domain("www.gazeta.pl") {

   body {
    background: #EEEEEE !important;
  }
}

@-moz-document domain("kobieta.gazeta.pl") {

  div.advertisement,
  div.advertisement + br {
    display:none !important;
  }
}
