css:
@-moz-document url-prefix("https://xenforo.com/community/"), domain("forums.spacebattles.com"), domain("forums.sufficientvelocity.com"), domain("forum.questionablequesting.com") {
.messageText span[style*="transparent"],
.messageText span[style*="Transparent"],
.messageText span[style*="TRANSPARENT"],
article span[style*="transparent"],
article span[style*="Transparent"],
article span[style*="TRANSPARENT"] { 
    text-decoration: underline dotted;
    text-decoration-color: inherit !important;
    background: #000000;
    /*color: #99CC00 !important;*/
    transition: 0s 180s;
}
.messageText span[style*="transparent"]:hover,
.messageText span[style*="TRANSPARENT"]:hover,
.messageText span[style*="Transparent"]:hover,
article span[style*="transparent"]:hover,
article span[style*="Transparent"]:hover,
article span[style*="TRANSPARENT"]:hover{
    color: #99CC00 !important;
    transition: 0s;
}
}
