    /* Root */
    body,
    div,
    tr,
    th,
    blockquote,
    footer,
    label,
    button,
    .breadcrumb {
        background-color: #282a36 !important;
        color: #f8f8f2 !important;
    }

    .nav-pills > li.active > a {
        background-color: #ad0505 !important;
        color: #f8f8f2 !important;
    }


    /* Resolve highcharts*/
    rect {
        fill: #282a36 !important;
        color: #282a36 !important;
    }

    text {
        fill: #f8f8f2 !important;
