css:
@-moz-document domain("smurf.mimuw.edu.pl") {
body,
#wrapper,
#container,
#squeeze,
#wrapper #container #center #squeeze,
.right-corner,
.left-corner,
#header-region,
.sticky {
    background: #333333 !important;
}

.error,
#footer,
.links {
    display: none;
}

.node {
    border-bottom: 0;
}

body {
    color: #eeeeee;
}

a:link,
a:visited {
    color: #498bcc;
    text-decoration: none;
}

#container {
    max-width: 40em !important;
    font-size: 22px;
    line-height: 2em;
    font-family: -apple-system, BlinkMacSystemFont, Segoe UI, Helvetica, Arial, sans-serif, Apple Color Emoji, Segoe UI Emoji;
}
}
