/* ==UserStyle==
@name           webvisor fix metrika.yandex.ru
@namespace      github.com/openstyles/stylus
@version        1.0.2
@description    09.08.2020, 11:35:13
@author         Mr.Foxss
==/UserStyle== */
@-moz-document regexp("https://metrika.yandex.ru/stat/visor.*") {
	.table_report_webvisor .table__body-row_viewed_yes .table__td:first-child,
	.table_report_webvisor .table__body-row_viewed_yes .table__td:last-child,
	.table_report_webvisor .table__body-row_viewed_yes .table__td {
		opacity: 1;
		color: white;
		background-color: dimgray;
	}
	.table_report_webvisor .table__body-row:hover .table__td,
	.table_report_webvisor .table__body-row_viewed_yes:hover .table__td:first-child {
		background-color: khaki;
		color: black;
	}
	.toggle-expand_theme_light .icon__minus,
	.toggle-expand_theme_light .icon__plus,
	.visit-favorite__flag-icon {
		opacity: 1;
	}
	.icon__plus,
	.icon__minus,
	.icon__flag {
		background-image: none;
	}
	.icon__plus:after,
	.icon__minus:after,
	.icon__flag:after {
		visibility: visible;
		opacity: 1;
	}
	.icon__plus:after {
		content: '\002B';
		font-size: 14pt;
	}
	.icon__minus:after {
		content: '\2212';
		font-size: 14pt;
	}
	.icon__flag:after {
		content: "\1F3C1";
	}
	.table_report_webvisor .table__body-row_viewed_yes .table__td .link_external_yes,
	.humanize-attributes__dimension-icon {
		background: white;
		background-repeat: no-repeat;
		padding: 3px 5px;
		border-radius: 5px;
		background-position: center;
		font-weight: bold;
		color: #333;
	}
	.table_report_webvisor .table__body-row_viewed_yes:hover .table__td .link_external_yes,
	.humanize-attributes__dimension-icon {
		background-color: khaki;
	}
	.b-page {
		min-width: 320px;
	}

	/* ширина ячеек */
	.table__th_name_ym-s-datetime {
		width: 97px !important;
	}
	.table__th_name_ym-s-screenresolutionwv {
		width: 80px !important;
	}
}
