css:
@-moz-document url-prefix("https://www.instagram.com//*[[name]]*//") {
.fDxYl:after {
content: "";
background: url(https://of-zeus.do.am/verifyinstagram.png) no-repeat 0; 

display: inline;
zoom: 1;
position: relative;
margin-left: 6px;
padding: 2px 16px 2px 0;
}
.page_verified {
display: inline;
zoom: 1;
position: relative;
margin-left: 0px;
padding: 2px 0px 2px 0;

}
.fDxYl::before{
 font-weight: normal; 
    color: #000; 
    font-weight: 600; 
    content: 'Подтвержденный';
    position: absolute;
padding: 3px;
opacity: 2px;
top: 39px;
left: 3px;
pointer-events: auto;
display: block;
border: 1px solid #fff;
background: #f8f4ff;
font-size: 12.5px;
width: 105px;
visibility: hidden;
margin-left: 140px;
}
.page_name, .fDxYl:hover:before{ visibility: visible;}
}
