css:
@-moz-document domain("medium.com"), domain("towardsdatascience.com"), domain("blog.usejournal.com"), domain("blog.insightdatascience.com") {
/* Disable */
  div:not(.ty) {
    background: transparent !important;
    background-color: transparent;
  }

  /* Text */
  div.u-textColorNormal, h1.u-textColorDark, p, span, div, figcaption, li, a, h1, h2, h3, h4 {
    color: #f8f8f2 !important;
  }

  /* Root */
  body {
    background-color: #282a36 !important;
    color: #f8f8f2 !important;
  }

  /* Popup */
  .bp {
    background-color: #282a36 !important;
    box-shadow: rgba(0, 0, 0, 0.3) 0px 0px 20px !important;
  }

  /* Logo */
  svg, .aw, .ax {
    fill: #f8f8f2 !important;
  }

  /* Buttons */
  button {
    border-color: #f8f8f2 !important;
  }

  button:selected {
    color: #f8f8f2 !important;
  }

  /* Navigation */
  nav {
    background-color: #6a737d33 !important;
    backdrop-filter: blur(10px) saturate(125%);
    padding-bottom: 5px;
  }

  .gist .highlight {
    background: #333;
  }

  .gist .blob-code, .gist .blob-num {
    color: #7a81a9;
  }

/* Custom code added by Perth */
mark { color: #000 !important; }

.uiScale .ui-h1, .uiScale .ui-h2, .uiScale .ui-h3, .uiScale .ui-h4, .uiScale .ui-summary, .uiScale .postMetaInline, .uiScale .ui-capsSubtle, .u-textColorLighter, .uiScale .ui-body, .u-resetSpectrum .u-baseColor--link.link--darker { color: #fff !important; }

.u-backgroundGrayLightest { background: rgba(0,0,0,0.5) !important; }

.popover--menu>.popover-inner { background: #282a36 !important; }

.popover--menu>.popover-inner a { color: #ccc !important; }
}
