css:
@-moz-document domain("angular.io") {
mat-sidenav.mat-sidenav.sidenav {
  bottom: 32px;
}
.app-toolbar {
  min-height: unset;
  height: 32px;
  top: unset !important;
  bottom: 0;
  background: white !important;
  color: inherit !important;
}
.app-toolbar * {
  color: inherit !important;
}
aio-search-box.search-container input {
  width: 100%;
  max-width: unset;
}
.app-toolbar .toolbar-external-icons-container {
  display: none;
}
.app-toolbar .nav-link.home {
  display: none;
}
.sidenav-content {
  padding: 0 !important;
}

}
