@-moz-document url("https://www.dreadcast.net/Main") {
/* LE DECK REBELLE TYPE SYLMERION */

  /* Image de fond du deck */
  .deck_type_4{background:url("https://i.imgur.com/N3nnWJa.png") 0 0 no-repeat;}

  /* couleur d'écriture de la zone de saisie */
  .deck_main input{color:#fb6262!important;font-family:monospace!important;}

  /* écriture de l'introduction du deck */
  .deck_type_4 .intro{color:#9a5656!important;font-family:monospace!important;}

  /* écriture du Perso@Matrice */
  .deck_type_4 .contexte{color:#c55151!important;font-family:monospace!important;}

  /* écriture du résultat */
  .deck_type_4 .ligne_resultat_fixed{color:#ffa0a0!important;font-family:monospace!important;}

  /* _________________________________ */

  /* LE DECK REBELLE TYPE LYRIA */

  /* Image de fond du deck */
  .deck_type_3 .deck_main{background:url("https://i.imgur.com/1rSSGeP.png");border: 1px solid #d70f0f;}

  /* contour du deck */
  .deck_type_3 .fond {background:url("https://i.imgur.com/fj1OzG3.png");}*

  /* écriture de l'introduction du deck */
  .deck_type_3 .intro{color:#c77070!important;font-family:monospace!important;}

  /* écriture du Perso@Matrice */
  .deck_type_3 .contexte{color:#f96666!important;font-family:monospace!important;}

  /* écriture du résultat */
  .deck_type_3 .ligne_resultat_fixed{color:#ffa0a0!important;font-family:monospace!important;}
}
