css:
@-moz-document domain("gmchosting.com") {
body {
    background-color: #222222;
    color: #ffffff;
}

.panel {
    background-color: #222222 !important;
    color: #ffffff !important;
}

div #console2 input {
    background-color: #222222 !important;
    color: #ffffff !important;
}

.form-group .input-group .input-group-md .has-feedback .has-success {
    background-color: #222222;
    color: #ffffff;
}

tr th {
    color: white;
}

tr td {
    background-color: #222222;
    color: #ffffff;
}

small {
    color: white !important;
}

h5 div {
    color: white !important;
}

.progress {
    background-color: #222222;
    font-weight: bold;
}

.progress span {
    color: unset;
}

.progress span #refresh {
    color: white !important;
}
}
