css:
@-moz-document url("https://blockchaindemo.io/") {
.ant-tabs-content.ant-tabs-content-animated {
    margin-left: 0 !important;
}
.ant-tabs-tabpane.more-shit {
    width:33% !important;
    padding:1% !important;
}
.ant-tabs:not(.ant-tabs-vertical)>.ant-tabs-content>.ant-tabs-tabpane-inactive {
    height: auto !important;
    opacity:0.7;
    padding: 1% !important;
}
.ant-tabs.ant-tabs-card>.ant-tabs-bar .ant-tabs-nav-wrap {
    position: fixed;
    top: 25%;
    right: 0;
    z-index:10;
}
.ant-card-body {
    padding:0;
}
.ant-tag {
    height:6em;
}
.ant-tag-text {
    word-break: break-all;
    font-size:16px;
}
}
