css:
/* i really want this to be global */
 :root {
	outline-color:  none  !important;
	outline-offset: unset !important;
	outline-style:  unset !important;
	outline-width:  0     !important;
	outline:        0     !important;
}
*, ::focus, ::active, ::after, ::before {
	outline : inherit;
}
