css:
@-moz-document domain("roblox.com") {
@keyframes rainbow {
	0% { background-color: #f00;border-color: #f00;}
	11.4% { background-color: #f80;border-color: #f80;}
	22.3% { background-color: #ff0;border-color: #ff0;}
	33.2% { background-color: #8f0;border-color: #8f0;}
	44.1% { background-color: #0f8;border-color: #0f8;}
	55% { background-color: #08f;border-color: #08f;}
	65.85% { background-color: #00f;border-color: #00f;}
	76.725% { background-color: #80f;border-color: #80f;}
	88.5% { background-color: #f08;border-color: #f08;}
	100% { background-color: #f00;border-color: #f00;}
}

a.btn-common-play-game-lg {
    animation: rainbow 15s infinite;
}
}
