css:
@-moz-document domain("pychess.org") {

selection.janggi-board.janggi > div > cg-helper > cg-container > cg-board > square.move-dest,
selection.janggi-board.janggi > div > cg-helper > cg-container > cg-board > square.premove-dest,
selection.janggi-board.janggi > div > cg-helper > cg-container > cg-board > square.last-move.move-dest{
    background: url(https://i.imgur.com/0aIICLG.png);
    background-repeat: no-repeat;
    background-position: center;
}
selection.janggi-board.janggi > div > cg-helper > cg-container > cg-board > square.move-dest:hover,
selection.janggi-board.janggi > div > cg-helper > cg-container > cg-board > square.premove-dest:hover {
    background: url(https://i.imgur.com/0aIICLG.png)!important;
    background-repeat: no-repeat!important;
    background-position: center!important;
    filter: contrast(200%);
}

selection.janggi-board.janggi > div > cg-helper > cg-container > cg-board > square.premove-dest {
    filter:hue-rotate(280deg);
}

selection.janggi-board.janggi > div > cg-helper > cg-container > cg-board > square.selected {
background: rgba(0,0,0,0.1);
    border-radius: 25px;
}

selection.janggi-board.janggi > div > cg-helper > cg-container > cg-board > square.oc.move-dest {
    background:radial-gradient(transparent 0%, transparent 80%, rgba(155, 10, 10, .75) 80%);
}
selection.janggi-board.janggi > div > cg-helper > cg-container > cg-board > square.oc.move-dest:hover {
    background:radial-gradient(transparent 0%, transparent 80%, rgba(155, 10, 10, .75) 80%)!important;
}


selection.janggi-board.janggi > div > cg-helper > cg-container > cg-board > square.last-move {
  will-change: transform;
  background:rgba(0,0,0,0.1);
}


}
