css:
@-moz-document domain("youtube.com")
{
  yt-icon.style-scope.ytd-toggle-button-renderer
  {
    display: none !important;
  }

  ytd-toggle-button-renderer.force-icon-button a.ytd-toggle-button-renderer
  {
    outline: none;
  }

  a.yt-simple-endpoint.ytd-toggle-button-renderer
  {
    outline: none;
  }

  ytd-toggle-button-renderer.style-text[is-icon-button]
  {
    background-image: url("https://i.imgur.com/1zArOLA.png") !important;
    background-size: 25px;
    background-repeat: no-repeat;
    background-position: 0px;
  }

  ytd-menu-renderer ytd-toggle-button-renderer.style-default-active[is-icon-button]:nth-of-type(1)
  {
    background-image: url("https://i.imgur.com/f2GZKAN.png") !important;
    background-size: 25px;
    background-repeat: no-repeat;
    background-position: 0px 18px !important;
    transform: translateY(-10px);
    transition: transform .5s, background-position .5s;
    transition-delay: 0s, .5s;
  }

  ytd-menu-renderer ytd-toggle-button-renderer.style-default-active[is-icon-button]:nth-of-type(2)
  {
    background-image: url("https://i.imgur.com/f2GZKAN.png") !important;
    background-size: 25px;
    background-repeat: no-repeat;
    background-position: 0px;
    transform: rotateX(180deg);
    padding-top: 4px;
    transition: transform .5s, padding-top .5s;
  }

  ytd-menu-renderer ytd-toggle-button-renderer.style-default-active[is-icon-button]:nth-of-type(1) >*
  {
    transition: transform .5s;
    transform: translateY(10px);
  }

  ytd-menu-renderer ytd-toggle-button-renderer.style-default-active[is-icon-button]:nth-of-type(2) >*
  {
    transition: transform .5s;
    transform: rotateX(-180deg);
  }
}

@-moz-document domain("youtube.com")
{
  #vote-count-left.ytd-comment-action-buttons-renderer[hidden] + #like-button.ytd-comment-action-buttons-renderer
  {
    background-size: 20px;
    background-position: 5px;
  }

  #like-button.ytd-comment-action-buttons-renderer, #dislike-button.ytd-comment-action-buttons-renderer
  {
    background-size: 20px;
    background-position: 5px;
    width: 32px;
  }

  ytd-toggle-button-renderer.style-default-active[is-icon-button]:nth-of-type(1)
  {
    background-image: url("https://i.imgur.com/f2GZKAN.png") !important;
    background-size: 25px;
    background-repeat: no-repeat;
    background-position: 5px 11px !important;
    transform: translateY(-5px);
    transition: transform .5s, background-position .5s;
    transition-delay: 0s, .5s;
  }

  ytd-toggle-button-renderer.style-default-active[is-icon-button]:nth-of-type(2)
  {
    background-image: url("https://i.imgur.com/f2GZKAN.png") !important;
    background-size: 25px;
    background-repeat: no-repeat;
    background-position: 40px;
    transform: rotate(180deg);
    transition: transform .5s, background-position .5s;
  }
}
