css:
::-webkit-scrollbar{
    width: 6px;
    height: 6px;
}


::-webkit-scrollbar-track {
	background-color: transparent;
}

::-webkit-scrollbar-thumb:vertical {
	border-radius: 3px;
	background-image: -webkit-gradient(linear,
									   left bottom,
									   left top,
									   color-stop(0.44, #d500fb),
									   color-stop(0.72, #fb00de),
									   color-stop(0.86, #fb0060));
}

::-webkit-scrollbar-thumb:horizontal {
	border-radius: 3px;
	background-image: -webkit-gradient(linear,
									   left top,
									   right top,
									   color-stop(0.44, #d500fb),
									   color-stop(0.72, #fb00de),
									   color-stop(0.86, #fb0060));
}
