css:
@-moz-document url-prefix("https://apps.facebook.com/mobaheroes"), url-prefix("https://i-heroes-fb.nextersglobal.com/") {
#mainContainer {
	position: fixed;
	top: 0;
	z-index: 350;
}
body {
    overflow: hidden;
}
#rightCol, .bg--heroes .customFooterContent:after, .bg--heroes .customFooterContent:before {
    display: none;
}
.bg--heroes #flash-wrapper:after, .bg--heroes #flash-wrapper:before {
    width: 0;
    height: 0;
}
#contentCol, #contentArea, .canvas_main_content, #iframe_canvas {
    width: 100vw;
    height: 100vh;
}
.bg--heroes-fb #flash-content {
    width: 100vw;
    height: calc(100vh)!important;
	position:fixed;
    top: 0;
    left: 0;
}
}
