css:
@-moz-document domain("youtube.com") {
ytd-masthead {
    color: #000;
    background: #20192e;
}
ytd-toggle-button-renderer.style-default-active[is-icon-button] {
    color: #ff6600;
}
html:not(.style-scope)[dark], :not(.style-scope)[dark] {
    --yt-spec-brand-background-solid: #20192e;
    --yt-spec-general-background-a: #20192e;
    --yt-spec-general-background-b: #20192e;
    --yt-spec-general-background-c: #20192e;
    --yt-spec-call-to-action: #ff6600;
    --yt-spec-static-brand-red: #ff6600;
    --yt-live-chat-header-background-color: #33284a;
    --yt-spec-brand-background-primary: #33284a;
    --paper-toggle-button-unchecked-button-color: #5628ad;
/*  --ytd-badge-icon-color: #ff6600; */
    --yt-live-chat-vem-background-color: #33284a;
}
#notification-count.ytd-notification-topbar-button-renderer {
    color: #FFF;
    background: #20192e;
    border: solid 0.1px;
}
#progress.ytd-thumbnail-overlay-resume-playback-renderer {
    background: #ff6600;
}
paper-button.ytd-subscribe-button-renderer {
    background: #6f00ff;
    border: solid 2px #878787;
    border-radius: 100px;
}
ytd-button-renderer.style-suggestive[is-paper-button] {
    border: solid 1px #ff6600;;
    border-radius: 100px;
}
.ytp-swatch-background-color, yt-icon-button.yt-live-chat-item-list-renderer {
    background: #ff6600;
}
ytd-player {
    background: #20192e;
}
ytd-page-manager {
    background: #20192e;
}
checkbox {
    background: #20192e;
    color: #20192e;
}
#newness-dot.ytd-guide-entry-renderer {
    background: #ff6600;
}
.guide-entry-badge.ytd-guide-entry-renderer {
   	color: #FFF;
    border: solid 2px #878787;
    background: #6f00ff;
    border-radius: 50px;
    padding: 3px;
    left: 5px;
}
ytd-topbar-logo-renderer path{
    fill: /*[[logo]]*/;
}
::-webkit-scrollbar {
    width: 12px;
}
::-webkit-scrollbar-track {
    -webkit-box-shadow: inset 0 0 6px rgba(0, 0, 0, 0.3);
    border-radius: 10px;
}
::-webkit-scrollbar-thumb {
    border-radius: 10px;
    -webkit-box-shadow: inset 0 0 6px rgba(0, 0, 0, 0.5);
}
::-webkit-scrollbar-track {
    background: #20192e;
}
::-webkit-scrollbar-thumb {
    background: #33284a;
}
/* by BadModer */
}
