css:
@-moz-document domain("mkb.gorlem.ml") {
body {background: #424242;
  color: rgb(255,255, 255); 
}

input,
select {
    color: #fff;
}

input::placeholder {
    color: #999;
}

input[type="submit"], input[type="button"],
a:not([class]) {
  color: rgb(172, 216, 255);
}

input[type="button"]:focus, input[type="button"]:active,
a:not([class]):hover, a:not([class]):focus, a:not([class]):active {
    color: rgb(62, 139, 206);
}

.table tbody tr:hover, .table .row:not(.row--head):hover {
    background: #4b4b4b; 
}
}
