css:
@-moz-document domain(codingame.com) {
    .cg-ide .console-bloc {
        top: auto;
        width: 100%;
        height: calc(100% - 350px);
    }
    .cg-ide-mini-leaderboard .leaderboard-item {
        margin: 10px 0px;
    }
    .cg-ide .console-bloc .mini-leaderboard {
        width: 150px;
    }
    .cg-ide .console-bloc.with-leaderboard .console-content {
        left: 150px;
    }
    .cg-player_container .gameBoxContainer {
        width: 600px;
        height: 300px;
    }
    .cg-last-battles .ranking-container {
        width: 780px;
    }
    .cg-ide .code-bloc {
        display: none;
    }
    .cg-ide .testcases-actions-container {
        top: 0;
        bottom: auto;
        height: 300px;
    }
}
