css:
@-moz-document domain("deno.land") {
img[alt="logo"] {
    filter: invert(1);
}
[class*="text"] {
    color: white;
}
[class*="text"], [class*="text"]:hover, [class*="bg"]:hover {
    color: white;
    background: none;
}
[class*="bg"], body {
    background: #222021;
}
select, option, input {
    color: black !important;
    background: white !important;
}
ol {
    counter-reset: item;
}
ol li {
    display: block;
}
ol li:before {
    content: counter(item)". ";
    counter-increment: item;
    color: white;
}
.markdown h1,.markdown h2, .markdown h3, .markdown h4, .markdown h5, .markdown p, .markdown ol, .markdown ul {
    color: white;
}
.markdown pre {
    filter: invert(1);
}
[for="search_field"] + div {
    display: flex;
    align-items: center;
}

[for="search_field"] + div .absolute {
    display: flex;
    position: relative;
}
[for="search_field"] + div input {
    margin-left: 10px;
    display: flex;
    height: 50%
}

.tsd-panel-group > h1, .tsd-panel-group > h2, .tsd-panel-group > h3, .tsd-navigation a, footer .tsd-legend, .tsd-generator {
    color: white;
}
}
