css:
@-moz-document url-prefix("https://www.livenirvana.com/concerts/"), url-prefix("https://www.livenirvana.com/sessions/"), url-prefix("https://www.livenirvana.com/interviews/"), url-prefix("https://www.livenirvana.com/art/"), url-prefix("https://www.livenirvana.com/posthumous/"), url-prefix("https://www.livenirvana.com/whoiswho/"), url-prefix("https://www.livenirvana.com/equipment/"), url-prefix("https://www.livenirvana.com/memorabilia/"), url-prefix("https://www.livenirvana.com/history/") {
/*** ana sayfa ***/
body, html {
    background: #252525!important;
    color: #e8e8e8!important;
}
.footerInner  {
	background: #1b1b1b!important;
	border-color: #333!important;
}
/*** sÃ¼tun ***/
.list-group-item {
    position: relative;
    display: block;
    padding: 10px 15px;
    margin-bottom: -1px;
    background-color: #252525;
    border: 1px solid #ddd;
}
a.list-group-item, button.list-group-item {
    color: #fff;
}
.table-index td a {
    color: #fff;
    text-decoration: none;
    display: block;
}
.footable-odd {
    background-color: #252525;
}
.table-striped>tbody>tr:nth-of-type(odd) {
    background-color: #2f2f2f;
}
/*** tuslar ***/
.btn-default {
    color: #969696;
    background-color: #2f2f2f;
    border-color: #ccc;
}
.pager li>a, .pager li>span {
    display: inline-block;
    padding: 5px 14px;
    background-color: #2f2f2f;
    border: 1px solid #ddd;
    border-radius: 15px;
}
.table-index td:hover {
    cursor: pointer;
    color: #555;
    text-decoration: none;
    background-color: #f5f5f5;
}
/*** alt ve ust taraf ***/
footer {
    padding-top: 10px;
    background-color: #252525;
    color: #fff;
    border-color: #080808;
}
.breadcrumb {
    padding: 8px 15px;
    margin-bottom: 20px;
    list-style: none;
    background-color: #2f2f2f;
    border-radius: 4px;
}

.panel-body {
    padding: 15px;
    background-color: #252525;
}
.panel-default>.panel-heading {
    color: #777;
    background-color: #2f2f2f;
    border-color: #ddd;
}
.panel-success>.panel-heading {
    color: #3c763d;
    background-color: #0a0a0a;
    border-color: #d6e9c6;
}
/*** tarih ***/
.date {
    background-color: rgba(47, 47, 47, 0.5);
}
}
