css:
@-moz-document url-prefix("https://learn.esperanto.com/") {

body {
	background-color: #0B0A0D;
	color: #D8BAF2;
}

div.footer {
	background-color: #1A1221;
	border-color: #1A1221;
}

div.popover {
	background-color: #1A1221;
	border-color: #1A1221;
}

div.panel {
	background-color: #2d0f46;
	border-color: #2d0f46;
}

div#teksto {
	background-color: #1A1221;
	border-color: #1A1221;
}

h3.popover-title {
	background-color: #2d0f46;
	border-color: #2d0f46;
}

h4.panel-title {
	background-color: #2d0f46;
	border: 12px;
	border-radius: 1px;
	border-style: solid;
	border-color: #2d0f46;
	margin-left: -15px;
	margin-right: -15px;
	margin-top: -10px;
	margin-bottom: -10px;
}

ul.nav {
	border: 0;
}

ul.nav.nav-tabs li.active a {
	background-color: #1e1228;
	border-color: #1e1228;
	color: #9c87ae;
}

ul.nav.navtabs li a:hover {
	background-color: #3b244f;
	border-color: #3b244f;
}

ul.pager li a {
	background-color: #1A1221;
	border-color: #1A1221;
}

li a {
	background-color: #131217;
	font-size: 20
}

a.btn.btn-default {
	background-color: #1e1228;
	border-color: #1e1228;
	color: #9c87ae;
}

a.btn.btn-default:hover {
	background-color: #3b244f;
	border-color: #3b244f;
}

p.alert {
	background-color: #1A1221;
	border-color: #1A1221;
}

input.form-control {
	background-color: #1A1221;
	color: #D8BAF2;
	border-color: #1A1221;
}

form.navbar-form selector#lingvoelektilo.form-control {
	background-color: #1A1221;
	color: #D8BAF2;
	border-color: #1A1221;
}

}
